{"changed":true,"filter":false,"title":"pacs_controller.rb","tooltip":"/app/controllers/pacs_controller.rb","value":"class PacsController < ApplicationController\n  before_action :set_pac, only: [:show, :edit, :update, :destroy]\n\n  # GET /pacs\n  # GET /pacs.json\n  def index\n    if params[:format] == nil\n      @pacs = Pac.all\n    else\n      @pacs = Pac.where(\"member_id = #{params[:format]}\")\n    end\n  end\n  \n  # GET /pacs/1\n  # GET /pacs/1.json\n  def show\n  end\n\n  # GET /pacs/new\n  def new\n    @pac = Pac.new\n  end\n\n  # GET /pacs/1/edit\n  def edit\n  end\n\n  # POST /pacs\n  # POST /pacs.json\n  def create\n    @pac = Pac.new(pac_params)\n\n    respond_to do |format|\n      if @pac.save\n        format.html { redirect_to @pac, notice: 'Pac was successfully created.' }\n        format.json { render :show, status: :created, location: @pac }\n      else\n        format.html { render :new }\n        format.json { render json: @pac.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # PATCH/PUT /pacs/1\n  # PATCH/PUT /pacs/1.json\n  def update\n    respond_to do |format|\n      if @pac.update(pac_params)\n        format.html { redirect_to @pac, notice: 'Pac was successfully updated.' }\n        format.json { render :show, status: :ok, location: @pac }\n      else\n        format.html { render :edit }\n        format.json { render json: @pac.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # DELETE /pacs/1\n  # DELETE /pacs/1.json\n  def destroy\n    @pac.destroy\n    respond_to do |format|\n      format.html { redirect_to pacs_url, notice: 'Pac was successfully destroyed.' }\n      format.json { head :no_content }\n    end\n  end\n\n  private\n    # Use callbacks to share common setup or constraints between actions.\n    def set_pac\n      @pac = Pac.find(params[:id])\n    end\n\n    # Never trust parameters from the scary internet, only allow the white list through.\n    def pac_params\n      params.require(:pac).permit(:PAC_Contribution, :member_id)\n    end\nend\n","undoManager":{"mark":34,"position":66,"stack":[[{"group":"doc","deltas":[{"start":{"row":8,"column":0},"end":{"row":14,"column":5},"action":"insert","lines":["def index","    if params[:format] == 'AllContinueedus'","      @continueedus = Continueedu.all","    else","      @continueedus = Continueedu.where(\"member_id = #{params[:format]}\")","    end","  end"]}]}],[{"group":"doc","deltas":[{"start":{"row":14,"column":5},"end":{"row":15,"column":0},"action":"insert","lines":["",""]},{"start":{"row":15,"column":0},"end":{"row":15,"column":2},"action":"insert","lines":["  "]}]}],[{"group":"doc","deltas":[{"start":{"row":8,"column":0},"end":{"row":8,"column":2},"action":"insert","lines":["  "]}]}],[{"group":"doc","deltas":[{"start":{"row":9,"column":40},"end":{"row":9,"column":41},"action":"remove","lines":["u"]}]}],[{"group":"doc","deltas":[{"start":{"row":9,"column":39},"end":{"row":9,"column":40},"action":"remove","lines":["d"]}]}],[{"group":"doc","deltas":[{"start":{"row":9,"column":38},"end":{"row":9,"column":39},"action":"remove","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":9,"column":37},"end":{"row":9,"column":38},"action":"remove","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":9,"column":36},"end":{"row":9,"column":37},"action":"remove","lines":["u"]}]}],[{"group":"doc","deltas":[{"start":{"row":9,"column":35},"end":{"row":9,"column":36},"action":"remove","lines":["n"]}]}],[{"group":"doc","deltas":[{"start":{"row":9,"column":34},"end":{"row":9,"column":35},"action":"remove","lines":["i"]}]}],[{"group":"doc","deltas":[{"start":{"row":9,"column":33},"end":{"row":9,"column":34},"action":"remove","lines":["t"]}]}],[{"group":"doc","deltas":[{"start":{"row":9,"column":32},"end":{"row":9,"column":33},"action":"remove","lines":["n"]}]}],[{"group":"doc","deltas":[{"start":{"row":9,"column":31},"end":{"row":9,"column":32},"action":"remove","lines":["o"]}]}],[{"group":"doc","delt