{"changed":true,"filter":false,"title":"payments_controller.rb","tooltip":"/app/controllers/payments_controller.rb","value":"class PaymentsController < ApplicationController\n  before_action :set_payment, only: [:show, :edit, :update, :destroy]\n  helper_method :current_user\n  # GET /payments\n  # GET /payments.json\n  def index\n    if params[:format] == 'AllPayments' || params[:format] == nil\n      @payments = Payment.all\n    else\n      @payments = Payment.where(\"member_id = #{params[:format]}\")\n    end\n  end\n\n  # GET /payments/1\n  # GET /payments/1.json\n  def show\n  end\n  \n  def show_pym_this_user\n    #@payment = current_user.Payment #I believe this is what we want -JK\n  end\n\n  # GET /payments/new\n  def new\n    @payment = Payment.new\n    #@payment = Payment.new :member_id => current_user.id #Depending on the amount, this will be used to auto fill. -JK\n    \n  end\n\n  # GET /payments/1/edit\n  def edit\n  end\n\n  # POST /payments\n  # POST /payments.json\n  def create\n    @payment = Payment.new(payment_params)\n    @payment.member_id = current_user.id #Creates the new payment as the active session user!\n    respond_to do |format|\n      if @payment.save\n        format.html { redirect_to @payment, notice: 'Payment was successfully created.' }\n        format.json { render :show, status: :created, location: @payment }\n      else\n        format.html { render :new }\n        format.json { render json: @payment.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # PATCH/PUT /payments/1\n  # PATCH/PUT /payments/1.json\n  def update\n    respond_to do |format|\n      if @payment.update(payment_params)\n        format.html { redirect_to @payment, notice: 'Payment was successfully updated.' }\n        format.json { render :show, status: :ok, location: @payment }\n      else\n        format.html { render :edit }\n        format.json { render json: @payment.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # DELETE /payments/1\n  # DELETE /payments/1.json\n  def destroy\n    @payment.destroy\n    respond_to do |format|\n      format.html { redirect_to payments_url, notice: 'Payment was successfully destroyed.' }\n      format.json { head :no_content }\n    end\n  end\n  \n  # Import CSV table\n  def import\n    Payment.import(params[:file])\n    redirect_to payments_url, notice: \"Products imported.\"\n  end\n  \n  private\n    # Use callbacks to share common setup or constraints between actions.\n    def set_payment\n      @payment = Payment.find(params[:id])\n    end\n\n    # Never trust parameters from the scary internet, only allow the white list through.\n    def payment_params\n      params.require(:payment).permit(:Pymt_Type, :Pymt_Amt, :Pymt_Date)  \n    end\nend\n","undoManager":{"mark":69,"position":100,"stack":[[{"group":"doc","deltas":[{"start":{"row":7,"column":14},"end":{"row":7,"column":15},"action":"insert","lines":["w"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":14},"end":{"row":7,"column":15},"action":"remove","lines":["w"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":14},"end":{"row":7,"column":15},"action":"insert","lines":["s"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":15},"end":{"row":7,"column":16},"action":"insert","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":16},"end":{"row":7,"column":17},"action":"insert","lines":["="]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":17},"end":{"row":7,"column":18},"action":"insert","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":18},"end":{"row":7,"column":19},"action":"insert","lines":["P"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":19},"end":{"row":7,"column":20},"action":"insert","lines":["a"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":20},"end":{"row":7,"column":21},"action":"insert","lines":["y"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":18},"end":{"row":7,"col